import axios from 'axios';
import React, { useEffect, useState } from 'react';
import './qrTablList.css'; // Import CSS file for styling
import { Link } from 'react-router-dom';

export default function QrTablList() {
    const [data, setData] = useState([]);
    const [myqr, setMyQR] = useState('');


    const getData = () => {
        axios.get("http://localhost:5000/generate").then((resp) => {
            console.log("Response:", resp.data);
            setData(resp.data);
        });
    };

    useEffect(() => {
        getData();
    }, []);

    function delData(id) {
        console.log("Delete data :", id);
        axios.delete("http://localhost:5000/generate/" + id).then(() => {
            getData();
        });
    }

    // Qr generator code
    // const generateQRCode = async () => {
    //     // console.log("Call qr generatore")
    //     try {
    //         const response = await axios.post(`http://localhost:5000/generate`, {
    //             name,
    //             fatherName,
    //             mobileNo,
    //             emergency,
    //             address,
    //             bloodGroup,
    //             insurance,
    //             companyName,
    //             insuranceName,
    //             insuranceNo,
    //             agentNo,
    //             expiryDate
    //         }
    //         );
    //         // console.log("Responce for post data :", response.data._id)
    //         setId(response?.data?._id)
    //     } catch (error) {
    //         console.error('Error generating QR code:', error);
    //     }
    // };
    return (
        <div className="container">
            <table>
                <thead>
                    <tr>
                        <th>S. No.</th>
                        <th>Name</th>
                        <th>Father name</th>
                        <th>Blood Group</th>
                        <th>Emergency number</th>
                        <th>Address</th>
                        <th>Operation</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((ele, i) => (
                        <tr key={ele._id}>
                            <td>{i + 1}</td>
                            <td>{ele.name}</td>
                            <td>{ele.fatherName}</td>
                            <td>{ele.bloodGroup}</td>
                            <td>{ele.emergency}</td>
                            <td>{ele.address}</td>
                            <td>
                                <button onClick={() => delData(ele._id)}>Delete</button>
                                <Link to={`/qrapp/${ele._id}`}>Edit</Link>
                                <button onClick={generateQRCode}>Generate QR Code</button>

                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {myqr && <img src={myqr} alt="QR Code" width={"200px"} />}
        </div>
    );
}
